const SkillCard = ({ skill }: { skill: Skill }) => (
  <Card className="group flex h-32 w-40 shrink-0 flex-col items-center justify-center p-4 transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-lg hover:shadow-primary/20">
    <CardContent className="flex flex-col items-center justify-center gap-2 p-2">
      <div className="h-12 w-12 flex items-center justify-center transition-all duration-300 ease-in-out group-hover:animate-glow">
        {skill.svg}
      </div>
      <span className="font-medium text-center">{skill.name}</span>
    </CardContent>
  </Card>
);

export function SkillsSection() {
  return (
    <section id="skills" className="w-full py-16 md:py-24 lg:py-32">
      <div className="container mx-auto px-4 md:px-6">
        <div className="mx-auto max-w-4xl space-y-8 text-center">
          <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl">
            <span className="text-primary">&gt;</span> ./show-skills
          </h2>
          <p className="text-lg text-muted-foreground md:text-xl">
            # A look at the technologies and tools I work with.
          </p>
        </div>
      </div>
      <div
        className="group relative mt-12 flex gap-6 overflow-hidden"
        style={{
          maskImage:
            "linear-gradient(to right, transparent 0%, black 10%, black 90%, transparent 100%)",
        }}
      >
        <div className="flex animate-marquee-horizontal items-center gap-6 group-hover:[animation-play-state:paused]">
          {[...skills, ...skills].map((skill, index) => (
            <SkillCard key={`${skill.name}-${index}`} skill={skill} />
          ))}
        </div>
      </div>
    </section>
  );
}

    